import React, { useState, useRef, useCallback } from 'react'
import Webcam from "react-webcam";
import styles from "../WebCam/WebCam.module.css";
import * as tf from '@tensorflow/tfjs';
import axios from 'axios'
import { Button, Box, Image, Flex, Text, CircularProgress } from '@chakra-ui/react'
import draw from '../Utilities'

function WebCam() {

    const [image, setImage] = useState('')
    const [progress, setProgress] = useState(true)
    const [clicked, setClicked] = useState()
    const videoConstraints = {
        width: 720,
        height: 360,
        facingMode: "user"
    };

    const webcamRef = useRef(null);

    const capture = useCallback(
        async () => {
            console.log("hello")
            try {
                const imageSrc = webcamRef.current.getScreenshot();
                setImage(imageSrc);
                const response = await axios.post('http://localhost:5000/img', { "base64image": imageSrc })
                setProgress(false);
                console.log(response);

            }
            catch (error) {
                console.log(error)
            }

        },
        [webcamRef]
    );
    return (
        <Flex justifyContent='center' alignItems='center' alignContent='space-between' direction='column'>
            <Webcam
                className={styles.cam}
                mirrored={true}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                screenshotQuality={1}
                height={360}
                width={720}
                videoConstraints={videoConstraints}
            ></Webcam>
            <Button size='md' width='40%' m='20px' colorScheme='blue' onClick={() => { capture() }}><Text>Capture</Text></Button>
            {
                progress ? <CircularProgress isIndeterminate color='green.300' /> : null
            }

            {/* {image ? <Box>
                <Image src={image}/>
            </Box> : null} */}
        </Flex>

    )
}

export default WebCam
