import React, { useState, useRef, useCallback } from 'react'
import Webcam from "react-webcam";
import styles from "../WebCam/WebCam.module.css";
import * as tf from '@tensorflow/tfjs';
import axios from 'axios'
import { Button, Box, Image, Flex, Text } from '@chakra-ui/react'
import draw from '../Utilities'

function WebCam() {

    const [image, setImage] = useState('')
    const videoConstraints = {
        width: 720,
        height: 360,
        facingMode: "user"
    };

    const webcamRef = useRef(null);
    const canvasRef = useRef(null);
    const blazeface = require('@tensorflow-models/blazeface');

    const runFacedetection = async () => {

        const model = await blazeface.load()
        console.log("FaceDetection Model is Loaded..")
        setInterval(() => {
            detect(model);
        }, 100);

    }

    const returnTensors = false;

    const detect = async (model) => {
        if (
            typeof webcamRef.current !== "undefined" &&
            webcamRef.current !== null &&
            webcamRef.current.video.readyState === 4
        ) {
            // Get video properties
            const video = webcamRef.current.video;
            const videoWidth = webcamRef.current.video.videoWidth;
            const videoHeight = webcamRef.current.video.videoHeight;

            //Set video height and width
            // webcamRef.current.video.width = videoWidth;
            // webcamRef.current.video.height = videoHeight;

            //Set canvas height and width
            canvasRef.current.width = videoWidth;
            canvasRef.current.height = videoHeight;

            // Make detections

            const prediction = await model.estimateFaces(video, returnTensors);

            // console.log(prediction)

            const ctx = canvasRef.current.getContext("2d");
            draw(prediction, ctx)
        }

    }


    // runFacedetection();

    const capture = useCallback(
        async () => {
            conso
            try {
                const imageSrc = webcamRef.current.getScreenshot();
                setImage(imageSrc);
                const response = await axios.post('http://localhost:5000/img', { "base64image": imageSrc })
                console.log(response);

            }
            catch (error) {
                console.log(error)
            }

        },
        [webcamRef]
    );
    return (
        <Flex justifyContent='center' alignItems='center' alignContent='space-between' direction='column'>
            <Webcam
                className={styles.cam}
                mirrored={true}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                screenshotQuality={1}
                height={360}
                width={720}
                videoConstraints={videoConstraints}
            ></Webcam>
            <canvas
                ref={canvasRef}
                style={{
                    position: "absolute",
                    marginLeft: "auto",
                    marginRight: "auto",
                    top: 100,
                    left: 0,
                    right: 80,
                    textAlign: "center",
                    zIndex: 9,
                    width: 640,
                    height: 480,
                }}
            />
            <Button size='md' width='40%' m='20px' colorScheme='blue' onClick={() => { capture() }}><Text>Capture</Text></Button>
            {/* {image ? <Box>
                <Image src={image}/>
            </Box> : null} */}
        </Flex>

    )
}

export default WebCam
