import React from 'react';
import NavBar from '../NavBar/NavBar';
import styles from '../Layout/Layout.module.css';
import Main from '../Main/Main'
import TeacherLogin from '../TeacherLogin/TeacherLogin';
import StudentLogin from '../StudentLogin/StudentLogin';
import StudentRegister from '../StudentRegister/StudentRegister'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ErrorPage from '../ErrorPage/ErrorPage';
import FormikContainer from '../FormikContainer/FormikContainer';
import { Container, Flex, Spacer } from '@chakra-ui/react';



function Layout() {

  useEffect(() => {
    async () => {
      try{
        const response = axios.get('http://localhost:5000/train')
      }
      catch(err){
        console.error(err)
      }
    }
  })


  return (
    <Router>
      <Flex width='100%' minHeight='100vh' direction='column' backgroundColor="gray.200" pt='10px' pl='30px' pr='30px'>
      <NavBar />
        <Routes>
          <Route path="/" element={<Main />}></Route>
          <Route path="/teacher-login" element={<TeacherLogin/>}></Route>
          <Route path="/student-login" element={<StudentRegister/>}></Route>
          <Route path="*" element={<ErrorPage/>}></Route>
        </Routes>
      </Flex>
    </Router>
  )
}

export default Layout
