import React, { useState, useRef, useCallback } from 'react'
import Webcam from "react-webcam";
import styles from "../WebCam/WebCam.module.css";
import * as tf from '@tensorflow/tfjs';
import axios from 'axios'
import { Button, Box, Image, Flex, Text, CircularProgress, Center, VStack } from '@chakra-ui/react'
import draw from '../Utilities'

function WebCam() {

    const [image, setImage] = useState('')
    const [isLoading, setIsLoading] = useState(false)
    const videoConstraints = {
        width: 720,
        height: 360,
        facingMode: "user"
    };

    const webcamRef = useRef(null);
    let result;
    const capture = useCallback(
        async () => {
            try {
                setIsLoading(true)
                const imageSrc = webcamRef.current.getScreenshot();
                setImage(imageSrc);
                const response = await axios.post('http://localhost:5000/img', { "base64image": imageSrc })
                if(response.data.name == -1){
                    result = <Text>Sorry no face detected !! please try again !!</Text>
                }else{
                    result = <Text></Text>
                }
                console.log(response);
                setIsLoading(false)
            }
            catch (error) {
                console.error("This is an error",error)
            }

        },
        [webcamRef]
    );
    return (
        <Flex justifyContent='center' alignItems='center' alignContent='space-between' direction='column'>
            <Webcam
                className={styles.cam}
                mirrored={true}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                screenshotQuality={1}
                height={360}
                width={720}
                videoConstraints={videoConstraints}
            ></Webcam>
            <Button size='md' width='40%' m='20px' colorScheme='blue' onClick={() => { capture() }}><Text>Capture</Text></Button>
            {
                isLoading ? (
                    <VStack>
                        <Center m='20px'>
                            <Text>Please wait face recognition is under process</Text>
                        </Center>
                        <Center>
                            <CircularProgress isIndeterminate color='blue.300' />
                        </Center>
                    </VStack>

                ) : null
            }

            { !isLoading ? <Text>Welcome to the class. Your attendance has been marked</Text> : null}

            {/* {image ? <Box>
                <Image src={image}/>
            </Box> : null} */}
        </Flex>

    )
}

export default WebCam
