import React, { useContext } from 'react';
import {
    Table,
    Thead,
    Tbody,
    Tfoot,
    Tr,
    Th,
    Td,
    TableCaption,
    Flex,
    Heading,
    Center,
    Text,
    Spacer
} from '@chakra-ui/react'
import { ApplicationContext } from '../Context/ApplicationContext'

function StudentList() {

    const { students } = useContext(ApplicationContext)

    useEffect(async() => {
        try {
          const res = axios.get('http://localhost:8000/student/students')
          console.log(res)
        } catch (error) {
          console.log(error)
        }

      })

    return (
        <Flex direction='column'>
            <Center borderBottom='1px'>
            <Text >Nadim Akhtar</Text>
            <Spacer/>
            <Text>{new Date().toLocaleString()}</Text>
            </Center>
            <Table colorScheme='blue.500' mt='20px'>
                <TableCaption color='black.500' placement='bottom'>students attendance table</TableCaption>
                <Thead>
                    <Tr>
                        <Th>Id</Th>
                        <Th>Name</Th>
                        <Th>Status</Th>
                    </Tr>
                </Thead>

                <Tbody>
                    {students.map(student => {
                        return (
                            <Tr key={student.id}>
                                <Td>{student.id}</Td>
                                <Td>{student.name}</Td>
                                <Td>{student.status}</Td>
                            </Tr>
                        )
                    })}
                </Tbody>

            </Table>
        </Flex>
    )
}

export default StudentList
