import React, {useCallback,useState,useRef}from 'react';
import { Formik, Form } from 'formik'
import Webcam from "react-webcam";
import FormikControl from '../FormikControl/FormikControl'
import * as Yup from 'yup'
import { Button, Center, Flex } from '@chakra-ui/react'


function StudentRegisterThree(props) {

    const webcamRef = useRef(null);
    const [image, setImage] = useState('')


    const validationSchema = Yup.object({
        studentImage : Yup.string().required('student image is required')
    })

    const handleSubmit = values => {
        props.next(values, true)
    }

    const videoConstraints = {
        width: 720,
        height: 360,
        facingMode: "user"
    };

    const capture = useCallback(
        () => {
            const imageSrc = webcamRef.current.getScreenshot();
            setImage(imageSrc);
        },
        [webcamRef]
    );



    return (
        <Formik initialValues={props.data} onSubmit={handleSubmit}>
            {
                (formik) => {
                    return (
                        <Center>
                            <Form style={{ "width": "40vw", "marginTop": "150px" }}>
                                <Flex direction='column' justifyContent='center' alignItems='center'>
                                <Webcam
                                    mirrored={true}
                                    ref={webcamRef}
                                    screenshotFormat="image/jpeg"
                                    screenshotQuality={1}
                                    height={360}
                                    width={720}
                                    videoConstraints={videoConstraints}
                                ></Webcam>
                                <Button size='md' width='40%' m='40px' colorScheme='blue' onClick={() => { capture() }}>Capture</Button>
                                </Flex>

                                <Button onClick={() => props.prev(formik.values)} colorScheme='teal' type='button' mt='20px' size='md'>Back</Button>
                                <Button colorScheme='teal' type='submit' mt='20px' size='md'>Submit</Button>
                            </Form>
                        </Center>
                    )
                }
            }
        </Formik>
    )
}

export default StudentRegisterThree
