import React from 'react';
import { Formik, Form } from 'formik'
import Webcam from "react-webcam";
import FormikControl from '../FormikControl/FormikControl'
import * as Yup from 'yup'
import { Button, Center, Flex } from '@chakra-ui/react'


function StudentRegisterThree(props) {

    const handleSubmit = values => {
        props.next(values, true)
    }

    const [image, setImage] = useState('')
    const videoConstraints = {
        width: 720,
        height: 360,
        facingMode: "user"
    };

    const capture = useCallback(
        async () => {
            try {
                setIsLoading(true)
                const imageSrc = webcamRef.current.getScreenshot();
                setImage(imageSrc);
                const response = await axios.post('http://localhost:5000/img', { "base64image": imageSrc })
                if(response.data.name == -1){
                    setResult(<Heading size='md'>Sorry no face detected !! please try again !!</Heading>)
                }else{
                    setResult(<Heading size='md'> Welcome {response.data.name} to the class. Your attendance has been marked</Heading>)
                }
                console.log(response);
                setIsLoading(false)
            }
            catch (error) {
                setResult(<Heading size='md'>Some error occured please check!!</Heading>)
                setIsLoading(false);
                console.error("This is an error",error)
            }

        },
        [webcamRef]
    );

    const webcamRef = useRef(null);


    return (
        <Formik initialValues={props.data} onSubmit={handleSubmit}>
            {
                (formik) => {
                    return (
                        <Center>
                            <Form style={{ "width": "40vw", "marginTop": "100px" }}>
                                <Webcam
                                    mirrored={true}
                                    ref={webcamRef}
                                    screenshotFormat="image/jpeg"
                                    screenshotQuality={1}
                                    height={360}
                                    width={720}
                                    videoConstraints={videoConstraints}
                                ></Webcam>
                                <Button onClick={() => props.handlePrevStep(fromik.values)} colorScheme='teal' type='button' mt='20px' size='md'>Back</Button>
                                <Button colorScheme='teal' type='submit' mt='20px' size='md'>Submit</Button>
                            </Form>
                        </Center>
                    )
                }
            }
        </Formik>
    )
}

export default StudentRegisterThree
