import React, { useState } from 'react'
import Webcam from "react-webcam";
import Button from '../Button/Button';
import styles from "../WebCam/WebCam.module.css";

function WebCam() {


    // function takeSnapshotThenUpload() {
    //   //get datauri
    //   let blob = convertToBlob(MOCK_DATA_URL)
    //   return uploadFile(blob)

    // }



    function convertToBlob(base64image) {
        // convert base64 to raw binary data held in a string
        var byteString = atob(base64image.split(',')[1]);
        // separate out the mime component
        var mimeString = base64image.split(',')[0].split(':')[1].split(';')[0];
        // write the bytes of the string to an ArrayBuffer
        var ab = new ArrayBuffer(byteString.length);
        var dw = new DataView(ab);
        for (var i = 0; i < byteString.length; i++) {
            dw.setUint8(i, byteString.charCodeAt(i));
            alert("arrived here");
        }

        // write the ArrayBuffer to a blob, and you're done
        return new Blob([ab], {
            type: mimeString
        });

    }

    function uploadFile(blob) {
        const formData = new FormData()
        formData.append('cancel.jpeg', blob)

        fetch('/saveImage', {
            method: 'POST',
            body: formData
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
            })
            .catch(error => {
                console.error(error)
            })
    }

    const [image, setImage] = useState('')
    const videoConstraints = {
        width: 720,
        height: 360,
        facingMode: "user"
    };

    const webcamRef = React.useRef(null);

    const capture = React.useCallback(
        () => {
            const imageSrc = webcamRef.current.getScreenshot();
            setImage(imageSrc);
        },
        [webcamRef]
    );
    return (
        <div className={styles.webcam}>
            <div className={styles.video}>
                <Webcam
                    className={styles.cam}
                    mirrored={true}
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    screenshotQuality={1}
                    height={360}
                    width={720}
                    videoConstraints={videoConstraints}
                ></Webcam>
                <Button click={() => { capture() }}></Button>
            </div>

            <img src={image}></img>
        </div>
    )
}

export default WebCam
